import { Container, Typography, Paper, Box, Avatar, Grid } from "@mui/material";
import StarIcon from "@mui/icons-material/Star";

const recensioni = [
  {
    nome: "Luca Bianchi",
    testo: "Servizio impeccabile, distribuzione precisa e puntuale!",
    stelle: 5,
  },
  {
    nome: "Maria Rossi",
    testo: "Ottima comunicazione e report fotografici molto utili.",
    stelle: 4,
  },
  {
    nome: "Giuseppe Verdi",
    testo: "Consigliato per chi cerca professionalità e affidabilità.",
    stelle: 5,
  },
  {
    nome: "Giuseppe Verdi",
    testo: "Consigliato per chi cerca professionalità e affidabilità.",
    stelle: 5,
  },
];

function stelleRender(stelle: number) {
  return Array.from({ length: stelle }).map((_, i) => (
    <StarIcon key={i} color="primary" />
  ));
}

export default function Recensioni() {
  return (
    <Container sx={{ mt: 8 }}>
      <Typography variant="h4" gutterBottom align="center">
        Recensioni dei clienti
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {recensioni.map((rec, index) => (
          <Grid item xs={12} sm={6} md={4} key={index} {...({} as any)}>
            <Paper
              sx={{
                p: 3,
                height: "100%",
                maxWidth: 360,
                mx: "auto", // центрирование
                border: "1px solid #ccc",
                borderRadius: 2, // скруглённые углы (по желанию)
              }}
            >
              <Box sx={{ display: "flex", alignItems: "center", mb: 3 }}>
                <Avatar sx={{ mr: 2 }}>{rec.nome.charAt(0)}</Avatar>
                <Typography variant="h6">{rec.nome}</Typography>
              </Box>
              <Typography variant="body1" sx={{ mb: 1 }}>
                "{rec.testo}"
              </Typography>
              <Box>{stelleRender(rec.stelle)}</Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}
