import {
  Container,
  Typography,
  Paper,
  Box,
  Avatar,
  Grid,
  Rating,
} from "@mui/material";
// eslint-disable-next-line @typescript-eslint/no-unused-vars
import StarIcon from "@mui/icons-material/Star";

const recensioni = [
  {
    nome: "Giulia R.",
    commento:
      "Servizio rapido e affidabile. Hanno distribuito tutto in tempo con grande professionalità!",
    valutazione: 5,
    foto: "/avatar1.jpg",
  },
  {
    nome: "Marco L.",
    commento:
      "Ottima comunicazione e rapporto qualità-prezzo. Sicuramente li ricontatterò.",
    valutazione: 4,
    foto: "/avatar2.jpg",
  },
  {
    nome: "Alessia B.",
    commento:
      "Hanno rispettato tutte le zone indicate e inviato report fotografici. Consigliati!",
    valutazione: 5,
    foto: "/avatar3.jpg",
  },
  {
    nome: "Alessia B.",
    commento:
      "Hanno rispettato tutte le zone indicate e inviato report fotografici. Consigliati!",
    valutazione: 5,
    foto: "/avatar3.jpg",
  },
];

export default function Reviews() {
  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Cosa dicono i nostri clienti
      </Typography>
      <Grid container spacing={3}>
        {recensioni.map((rec, i) => (
          <Grid item xs={12} md={4} key={i} {...({} as any)}>
            <Paper elevation={3} sx={{ p: 3 }}>
              <Box sx={{ display: "flex", alignItems: "center", mb: 2 }}>
                <Avatar src={rec.foto} sx={{ mr: 2 }} />
                <Box>
                  <Typography variant="subtitle1">{rec.nome}</Typography>
                  <Rating value={rec.valutazione} readOnly size="small" />
                </Box>
              </Box>
              <Typography variant="body2">“{rec.commento}”</Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}
